section Section1;
shared ktg1CSVwrangler = let
  AdfDoc = AzureStorage.DataLakeContents("https://ktg1blob.dfs.core.windows.net/adf/flights/flightsFull.csv"),
  Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv])
in
  Csv;
shared UserQuery = let
  Source = ktg1CSVwrangler
in
  Source;
